mapper.select:
select * from student


    and `studentid` = #{studentid}


    and `name` like concat('%',#{name},'%')



    and `schoolid`in
    (
      select id from school


          and `name` = #{schoolname}


          and `areacode` = #{areacode}


          and `schooltype` = #{schooltype}


    )





    order by ${sidx} ${order}


    order by studentid asc



  limit #{offset}, #{limit}
