mapper.select:
select * from teacher


    and `name` like concat('%',#{name},'%')



    and `schoolcode`in
    (
    select schoolcode from school


        and `name` = #{schoolname}


        and `areacode` = #{areacode}


        and `schooltype` = #{schooltype}


    )





    order by ${sidx} ${order}


    order by teacherid asc



  limit #{offset}, #{limit}

select COALESCE(count(*),0) from teacher


    and `name` like concat('%',#{name},'%')



    and `schoolcode`in
    (
    select schoolcode from school


        and `name` = #{schoolname}


        and `areacode` = #{areacode}


        and `schooltype` = #{schooltype}


    )





    order by ${sidx} ${order}


    order by teacherid asc



  limit #{offset}, #{limit}

mapper.insert:
insert into teacher (id, teacherid, name, 
  sex, classname, school_id
  )
values (#{id,jdbcType=BIGINT}, #{teacherid,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{sex,jdbcType=VARCHAR}, #{classname,jdbcType=VARCHAR}, #{schoolId,jdbcType=VARCHAR}
