mapper.select:
select * from teacher where name = name and school_id IN ( select school_id from school where name=schoolename and areacode in( select areacode from ba_area where name = areaname ) and school_type_id IN ( select school_type_id from schooltype where schooltype=schooltype ) )
select * from teacher
and `name` = #{name}
and `schoolid`in ( select schoolid from school
and `name` = #{schoolname}
and `areacode` = #{areaname}
and `schooltype` = #{schooltype}
)
limit #{offset}, #{limit}

mapper.update:
update teacher
set name = #{name,jdbcType=VARCHAR},
  sex = #{sex,jdbcType=VARCHAR},
  classname = #{classname,jdbcType=VARCHAR},
  schoolcode = #{schoolcode,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
