mapper.select:
select
a.id, a.account, a.username, a.organcode, a.departcode, a.postcode
from user_info a
where a.is_delete = 0

    and a.account = #{param.account}


    and a.username = #{param.username}

ORDER by a.create_time desc
limit #{start}, #{pageSize}

select count(1)
from user_info a
where a.is_delete = 0

    and a.account = #{param.account}


    and a.username = #{param.username}


    and exists(
        select 1 from organization b where b.organcode = a.organcode
        and b.name = #{param.organName}
    )


    and exists(
        select 1 from depart b where b.departcode = a.departcode
        and b.name = #{param.departName}
    )


    and exists(
        select 1 from post b where b.postcode = a.postcode
        and b.name = #{param.postName}
    )

mapper.delete:
delete from user_info
where is_delete = 0
and id = #{id,jdbcType=INTEGER}

mapper.select:
select

from user_info
where is_delete = 0
and account = #{account}
and password = #{pwd}

mapper.update:
update user_info
set password = #{newPwd}
where is_delete = 0
and account = #{account}
